				// INSTAGRAM FEED
				
				$(document).ready(function(){
				
					var token = $('.section.instagram').data('token'),
						container = $('.section.instagram .thumbnail-wrapper .entry-link'),
						num_photos = container.length;
				
					$.ajax({
